import React from "react";
import { Box, Button, Flex, Image, Link, Spacer } from "@chakra-ui/react";
import Twitter from "./assets/social-media/twitter_32x32.png";
import Discord from "./assets/social-media/discord_32x32.png";
import Github from "./assets/social-media/images.png";
import Docs from "./assets/social-media/docs_32x32.png";




const NavBar = ({ accounts, setAccounts }) => {
    const isConnected = Boolean(accounts[0]);

    async function connectAccount() {
        if (window.ethereum) {
            const accounts =await window.ethereum.request({
                method: "eth_requestAccounts",
            });
            setAccounts(accounts);
        }
    }

    return (
        <Flex justify="space-between" align="center" padding="30px">
            {/* Left Side-Social Media Icons*/}
            <Flex justify="space-around" width="40%" padding="0 75px">
                <Link href="https://twitter.com/Quanionxyz">
                <Image src={Twitter} boxSize="32px" margin="0 15px" />
                </Link>
                <Link href="https://discord.gg/nHmymppa">
                <Image src={Discord} boxSize="32px" margin="0 15px" />
                </Link>
                 <Link href="https://github.com/Quanionxyz">
                <Image src={Github} boxSize="32px" margin="0 15px" />
                </Link>
                <Link href="https://quanion.gitbook.io/quanion-docs">
                <Image src={Docs} boxSize="32px" margin="0 15px" />
                </Link>
            </Flex>

                        {/* Right Side-Social Media Icons*/}
            <Flex justify="space-arround" align="center" width="40px" padding="30px">
            <Box margin="0 15px">About</Box>
            <Spacer />
            <Box margin="0 15px">Mint</Box>
            <Spacer />
            <Box margin="0 15px">Team</Box>
            <Spacer />

            {/* Connect */}
            {isConnected ? (
            <Box margin="0 15px">Connected</Box>
            ) : (
            <Button
            backgroundColor="#D6517D"
            borderRadius="5px"
            boxShadow="0px 2px 2px 1px #0F0F0F"
            color="white"
            cursor="pointer"
            fontFamily="inherit"
            padding="15px"
            margin="0 15px"
            onClick={connectAccount}
            >
                Connect
            </Button>
            )}
            </Flex>
        </Flex>



    );
};

export default NavBar;
